import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { createReview, detailsProduct } from "../actions/productActions";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import styled from "styled-components";
import Rating from "../components/Rating";
import { PRODUCT_REVIEW_CREATE_RESET } from "../constants/productConstants";

export default function ProductScreen(props) {
  const dispatch = useDispatch();
  const productId = props.match.params.id;
  const [qty, setQty] = useState(1);
  // const [size, setSize] = useState("")
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;
  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;
  console.log(qty)
  const productReviewCreate = useSelector((state) => state.productReviewCreate);
  const {
    loading: loadingReviewCreate,
    error: errorReviewCreate,
    success: successReviewCreate,
  } = productReviewCreate;

  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  useEffect(() => {
    if (successReviewCreate) {
      window.alert("Review Submitted Successfully");
      setRating("");
      setComment("");
      dispatch({ type: PRODUCT_REVIEW_CREATE_RESET });
    }
    dispatch(detailsProduct(productId));
  }, [dispatch, productId, successReviewCreate]);
  const addToCartHandler = () => {
    props.history.push(`/cart/${productId}?qty=${qty}`);
  };
  const submitHandler = (e) => {
    e.preventDefault();
    if (comment && rating) {
      dispatch(
        createReview(productId, { rating, comment, name: userInfo.name })
      );
    } else {
      alert("Please enter comment and rating");
    }
  };
  return (
    <Wrapper>
      <div>
        {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : (
          <div>
            <Link to="/">Back to result</Link>
            <div className="main">
              <div className="productimg">
                <img src={product.image} alt={product.name}></img>
              </div>
              <div className="content ">
                <h1 className="text-[45px] font-bold sm:text-15xl text-black text-gradient py-1">
                  {product.name}
                </h1>
                <h6 className="font-bold text-[#08AD76]">
                  SKU: <span>#089754</span>
                </h6>
                <h3 className="mt-[8px] mb-[8px] text-[28px] font-semibold">
                  {" "}
                  ${product.price}
                </h3>
                <p className="text-[16px] mt-[8px] font-normal">
                  {product.description}
                </p>
                <Rating
                  rating={product.rating}
                  numReviews={product.numReviews}
                ></Rating>

                <hr />
                <div>
                  <span className="font-bold">Avialibity: </span>
                  <span>
                    {product.countInStock > 0 ? (
                      <span>In Stock</span>
                    ) : (
                      <span>Unavailable</span>
                    )}
                  </span>
                </div>

                {product.countInStock > 0 && (
                  <>

                    <li>
                      <div className="flex  justify-between w-[15%]">
                        <div>Qty</div>
                        <div>
                          <select
                            className=" p-[0px] "
                            value={qty}
                            onChange={(e) => setQty(e.target.value)}
                          >
                            {[...Array(product.countInStock).keys()].map(
                              (x) => (
                                <option key={x + 1} value={x + 1}>
                                  {x + 1}
                                </option>
                              )
                            )}
                          </select>
                        </div>
                      </div>
                    </li>
                    <li>
                      <button
                        onClick={addToCartHandler}
                        type="button"
                        className="flex flex-row w-72 justify-center items-center my-5 bg-[#08AD76] p-3  cursor-pointer"
                      >
                        <p className="text-white text-base font-semibold">
                          Add to Bag
                        </p>
                      </button>
                    </li>
                  </>
                )}
              </div>
            </div>
            <div className="mt-[50px] ">
              <h2 id="reviews" className="text-[20px] m-[0px] font-bold">
                Reviews
              </h2>
              {product.reviews.length === 0 && (
                <MessageBox>There is no review</MessageBox>
              )}
              <ul className="w-[100%]  m-[0px]">
                {product.reviews.map((review) => (
                  <li key={review._id}>
                    <strong>Customer Name: {review.name}</strong>
                    <Rating rating={review.rating} caption=" "></Rating>
                    <p>Date: {review.createdAt.substring(0, 10)}</p>
                    <p>Comment: {review.comment}</p>
                  </li>
                ))}
                <li>
                  {userInfo ? (
                    <form className="formreview" onSubmit={submitHandler}>
                      <div>
                        <h2 className="text-[20px] m-[0px] font-bold">
                          Write a customer review
                        </h2>
                      </div>
                      <div>
                        <label htmlFor="rating">Rating</label>
                        <select
                          id="rating"
                          value={rating}
                          onChange={(e) => setRating(e.target.value)}
                        >
                          <option value="">Select...</option>
                          <option value="1">1- Poor</option>
                          <option value="2">2- Fair</option>
                          <option value="3">3- Good</option>
                          <option value="4">4- Very good</option>
                          <option value="5">5- Excelent</option>
                        </select>
                      </div>
                      <div>
                        <label htmlFor="comment">Comment</label>
                        <textarea
                          id="comment"
                          value={comment}
                          onChange={(e) => setComment(e.target.value)}
                        ></textarea>
                      </div>
                      <div>
                        <label />
                        <button
                          className="bg-[#08AD76] text-[white]"
                          type="submit"
                        >
                          Submit
                        </button>
                      </div>
                      <div>
                        {loadingReviewCreate && <LoadingBox></LoadingBox>}
                        {errorReviewCreate && (
                          <MessageBox variant="danger">
                            {errorReviewCreate}
                          </MessageBox>
                        )}
                      </div>
                    </form>
                  ) : (
                    <MessageBox>
                      Please <Link to="/signin">Sign In</Link> to write a review
                    </MessageBox>
                  )}
                </li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </Wrapper>
  );
}

