import React from "react";
import styled from "styled-components";
import { BsPlayCircleFill } from "react-icons/bs";
const Record = (props) => {
  const { record } = props;
  return (
      <div className="w-[370px] h-[100%] bg-[#F3F0F0] flex flex-wrap justify-between  mt-[20px]">
        <div className="relative">
          <div className="h-[200px]">
            <img
              className="w-[370px] h-[100%]"
              src={record.image}
              alt={record.title}
            />
          </div>
          <Link href={record.url}>
          <div className="absolute playbtn">
            <BsPlayCircleFill />
          </div>

          </Link>
        </div>
        <div className="w-[100%] flex justify-between items-center">
          <h3 className="font-semibold">{record.title}</h3>
          <p className="bg-[#256F98] p-[10px] text-[#FFF]">74:52</p>
        </div>
      </div>
  );
};

export default Record;
