import React,{useRef} from "react";
import emailjs from '@emailjs/browser';
import styled from "styled-components";
const Contact = () => {





  return (
    <Wrapper>
    <div className=" w-[100%] h-[85vh] flex justify-between maincontact">
      <div className=" w-[50%] imgcontent">
        <img
          className="w-[100%] h-[100%]"
          src="https://images.pexels.com/photos/162568/oil-pump-jack-sunset-clouds-silhouette-162568.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
          alt="register"
        />
        <h2 >Contact with us</h2>
      </div>
      <div className="w-[70%]  flex items-center justify-center formcontent">
        <form
        ref={form}
          onSubmit={submitHandler}
          className="w-[100%] flex flex-col items-center"
        >
          <h1 className="font-bold text-[60px] text-center">Get in Touch</h1>
          <p className="text-center">
            Have an inquiry or some feedback for us? Fill put the form below to
            contact our team.
          </p>
          <div className="w-[50%]  flex justify-between mt-[15px] inputs">
            <div className="flex flex-col">
              <label className="font-semibold mt-[20px] ">First Name</label>
              <input
                name="name"
                type="text"
                placeholder="Please enter name"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
              />
            </div>
            <div className="flex flex-col">
              <label className="font-semibold mt-[20px]" >Last Name</label>
              <input
              name="lastname"
                type="text"
                placeholder="Please enter name"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
              />
            </div>
          </div>
          <label className="font-semibold mt-[20px] text-left" >Email Adress</label>
          <input
            name="reply_to"
            type="email"
            id="email"
            placeholder="Please enter email address"
            className="w-[50%] bg-[#E8F0FE] p-[10px] "
          />
          <label className="font-semibold mt-[20px]" >Message</label>
          <textarea
            name="message"
            type="text"
            id="message"
            placeholder="Hey friends, lets talk about"
            className="w-[50%] h-[150px] bg-[#E8F0FE] p-[10px] "
          />
          <input
            type="submit"
            value="SEND"
            className="bg-[#000] w-[50%] text-[white] p-[10px] mt-[15px]"
          />
          
        </form>
      </div>
    </div>
    </Wrapper>
  );
};

export default Contact;


const Wrapper = styled.div`
@media (max-width: 768px) {
  .maincontact {
  width: 100%;
  flex-direction: column;
  height: 100%;
  
}
.imgcontent {
  position: relative;
  width: 100%;
}
.imgcontent h2 {
text-align: center;
 position: absolute;
 font-size: 25px;
 left: 50%;
 transform: translate(-50%,-50%);
 top: 50%;
 color: white;
 font-weight: bold;
}
.inputs {
  width: 90%;
}
.inputs input {
  width: 100%;
}
.formcontent {
  width: 100%;
  padding-bottom: 15px;
}
.formcontent input,button,textarea {
  width: 90%;
}
.formcontent h1 {
  font-size: 25px;
}
}
`
