import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { BsPlayCircleFill } from "react-icons/bs";
const Record = (props) => {
  const { record } = props;
  return (
    <Wrapper>
      <div className="record">
        <div className="relative">
          <div className="flex justify-center items-center">
            <img
              className="w-[100%] h-[100%]"
              src={record.image}
              alt={record.title}
            />
          </div>
          <Link to={record.url}>
            <div className="absolute playbtn">
              <BsPlayCircleFill />
            </div>
          </Link>
        </div>
        <div className="w-[100%] flex justify-between items-center p-[10px] ">
          <h3 className="font-semibold">{record.title} | {record.author}</h3>
        </div>
      </div>
    </Wrapper>
  );
};

export default Record;

const Wrapper = styled.div`
width: 100%;
height: 100%;
padding: 20px;
display: flex;
flex-direction: column;
align-items: center;

.record {
  width: 100%;
  max-width: 350px;
  max-height: 350px;
  height: 100%;
  box-shadow: rgba(0, 0, 0, 0.16) 0px 1px 4px;
  margin-top: 10px;
}

img {
  width: 100%;
  height: 100%;   
  max-height: 350px;
  object-fit: cover;
}
  @media (max-width: 768px) {
    padding: 10px;
  }

  .playbtn {
    font-size: 65px;
    color: #256f98;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    cursor: pointer;
  }
  .playbtn:hover {
    font-size: 85px;
    transition: 1s ease-in-out;
  }
`;
