import React from 'react'
import styled from 'styled-components'
import {BsFillPlayFill} from 'react-icons/bs' 
const Container = styled.div`
    min-width: 350px;
    height: 200px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    position: relative;
    margin: 20px;
    position: relative;
`
const Image = styled.img`
    height: 90%;
    width: 90%;
    object-fit: cover;
`
const Button = styled.button`
font-size: 1.5rem;
color: white;
    border: none;
    padding: 30px;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    background:red;
    transition: all 0.5s ease;
    &:hover{
        background: #0492C4;
    }
`
const PlayContainer = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 80%;
    display: flex;
    justify-content: center;
    align-items: center;
`
const InfoContainer = styled.div`
    padding: 10px 0;
    width: 90%;
    background: #242424;
    color: white;
    font-weight: 500;
`

const RecordItem = ({record}) => {
  
  return (
    <Container>
        <Image src={record.image}></Image>
        <InfoContainer>{record.title}</InfoContainer>
        <PlayContainer>
            <Button>
        {/* <BsFillPlayFill/> */} 2
            </Button>
        </PlayContainer>
    </Container>
  )
}

export default RecordItem
