import React from "react";
import styled from "styled-components";
import { FaInstagram, FaFacebook } from "react-icons/fa";

const Footer = () => {
  return (
    <Container>
      <div className="footer">
        <div className="nav-header">
          <a href="/">
            <h2 className="navtitle">P-TSHIRT</h2>
          </a>
        </div>
        
          <div>
            About
            <ul>
              <li>Blog</li>
              <li>Press</li>

              <li>Order Tracking</li>
            </ul>
          </div>

          <div>
            Help
            <ul>
              <li>Contact US</li>
              <li>Customer Service</li>
              <li>Returns & Refunds</li>
              <li>FAQ</li>
            </ul>
          </div>

          <div>
            Social Media
            <ul>
              <li>Facebook</li>
              <li>Instagram</li>
              <li>Pinterest</li>
              <li>Tumbir</li>
            </ul>
          </div>
        
      </div>
    </Container>
  );
};

const Container = styled.footer`
  height: 50vh;
  width: 100%;
  display: flex;
    background: #203040;
  color: #fff;
  
  

  h5 {
    color: white;
    margin: 0.1rem;

    font-weight: 400;
    text-transform: none;
    line-height: 1.25;
  }
  .navtitle {
    font-style: normal;
    font-weight: bolder;
    font-size: 36px;
    line-height: 54px;
    color: var(--orange-color);
  }

  .footer {
    width: 80%;
    display: flex;
    justify-content: space-between;
    margin-top: 30px;
    margin: auto;
  }
  .footerinfo div {
    text-align: left;
  }
  .footer ul {
    margin-top: 10px;
  }
  .footer .icons {
    display: flex;
    font-size: 1.5rem;
    padding: 10px;
    padding-top: 0px;
  }

  @media (max-width: 776px) {
    flex-direction: column;
    height: auto;
    justify-content: center;
    background: #2b2c2f;
    text-align: center;
    padding-bottom: 10px;
    .footer {
      flex-direction: column;
    }
    ul li {
        margin-top: 10px;
    }
  }
`;

export default Footer;
