import React, { useEffect } from "react";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from "react-redux";
import { listBooks } from "../actions/bookActions";
import styled from "styled-components";
import Book from "../components/Book";
export default function HomeScreen() {
  const dispatch = useDispatch();
  const bookList = useSelector((state) => state.bookList);
  const { loading, error, books } = bookList;

  useEffect(() => {
    dispatch(listBooks());
  }, [dispatch]);

  console.log(books);

  return (
    <Wrapper>
      <div>
        <div className="imgdiv">
          <img className="img" src={aboutimg} />
          <div className="abouttext">
            <h2>Accelerating research discovery to shape a better future</h2>
            <h1>Today's research, tomorrow's innovation</h1>
            <input
              type="text"
              onChange={(event) => {
                setSearchTerm(event.target.value);
              }}
              className="w-[70%] h-[auto] p-[10px]"
              placeholder="Search something"
            />
          </div>
        </div>

        <div className="books mt-[80px]">
          {loading ? (
            <LoadingBox></LoadingBox>
          ) : error ? (
            <MessageBox variant="danger">{error}</MessageBox>
          ) : (
            <div className="w-[80%] m-[auto] mainbooks flex justify-between flex-wrap">
              {books
                .filter((val) => {
                  if (searchTerm === "") {
                    return val;
                  } else if (
                    val.name.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return val;
                  }
                })
                .map((book) => (
                  <div key={book.id} className="bookcard">
                    <a href={book.url}>
                      <img src={book.img} alt={book.name} className="cardimg" />
                    </a>
                    <div>
                      <h2 className="font-bold text-[20px]">{book.name}</h2>
                      <div className="text-[15px] p-[10px] bg-[#256F98] text-[white] w-[100%] downloadbtn">
                        Download Now
                      </div>
                    </div>
                  </div>
                ))}
            </div>
          )}
        </div>
      </div>
    </Wrapper>
  );
}
