import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { register } from "../actions/userActions";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import styled from "styled-components";
export default function RegisterScreen(props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const redirect = props.location.search
    ? props.location.search.split("=")[1]
    : "/";

  const userRegister = useSelector((state) => state.userRegister);
  const { userInfo, loading, error } = userRegister;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert("Password and confirm password are not match");
    } else {
      dispatch(register(name, email, password));
    }
  };
  useEffect(() => {
    if (userInfo) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, userInfo]);

  const universities = ["Khazar University"]

  return (
    <Wrapper>
      <div className=" w-[100%] h-[85vh] flex justify-between mainlogin">
        <div className="w-[100%]  flex items-center justify-center input">
          <form className="form" onSubmit={submitHandler}>
            <div>
              <h1 className="font-bold text-[60px] text-center">SIGN UP</h1>
            </div>
            {loading && <LoadingBox></LoadingBox>}
            {error && <MessageBox variant="danger">{error}</MessageBox>}
            <div>
              <label  className="mt-[15px]" htmlFor="name">Name</label>
              <input
                type="text"
                id="name"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
                placeholder="Enter name"
                required
                onChange={(e) => setName(e.target.value)}
              ></input>
            </div>
            <div>
              <label  className="mt-[15px]" htmlFor="email">Email address</label>
              <input
                type="email"
                id="email"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
                placeholder="Enter email"
                required
                onChange={(e) => setEmail(e.target.value)}
              ></input>
            </div>
            <div>
              <label  className="mt-[15px]" htmlFor="university">University</label>
              <select
                type="text"
                id="university"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
                placeholder="Enter University"
                required
                onChange={(e) => setEmail(e.target.value)}>

                <option value="1">University of Science and Technology of Vietnam</option>
              </select>
            </div>
            <div>
              <label  className="mt-[15px]" htmlFor="email">Email address</label>
              <input
                type="email"
                id="email"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
                placeholder="Enter email"
                required
                onChange={(e) => setEmail(e.target.value)}
              ></input>
            </div>
            <div>
              <label  className="mt-[15px]" htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                className="w-[100%] bg-[#E8F0FE] p-[10px] "
                placeholder="Enter password"
                required
                onChange={(e) => setPassword(e.target.value)}
              ></input>
            </div>
            <div>
              <label  className="mt-[15px]" htmlFor="confirmPassword">Confirm Password</label>
              <input
                type="password"
                id="confirmPassword"
                className="w-[100%] bg-[#E8F0FE] p-[10px]"
                placeholder="Enter confirm password"
                required
                onChange={(e) => setConfirmPassword(e.target.value)}
              ></input>
            </div>
            <div>
              <label />
              <button
                className="bg-[#000] w-[100%] text-[white] p-[10px] mt-[15px]"
                type="submit"
              >
                Register
              </button>
            </div>
            <div>
              <label />
              <div className="loginfooter">
                Already have an account?{" "}
                <Link to={`/signin?redirect=${redirect}`}>Sign-In</Link>
              </div>
            </div>
          </form>
        </div>
      </div>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  .imgcontent {
    position: relative;
  }
  .imgcontent h2 {
    position: absolute;
    font-size: 40px;
    color: white;
    top: 30%;
  }

  .imgcontent p {
    position: absolute;
    font-size: 40px;
    color: white;
    top: 40%;
  }
  @media (max-width: 768px) {
    .mainlogin {
      height: auto;
      flex-direction: column;
    }
    .imgcontent {
      display: none;
    }
    .input {
      width: 100%;
      margin-top: 60px;
    }
    .input h1 {
      font-size: 25px;
    }
    .input input {
      width: 90%;
    }
    .input button {
      width: 90%;
    }
  }
`;
