import React, { useEffect } from "react";
import Product from "../components/Product";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Subscribe from "../components/Subscribe";
import { useDispatch, useSelector } from "react-redux";
import { listProducts } from "../actions/productActions";
import { listBlogs } from "../actions/blogActions";
import Testimontials from "../components/Testimontials";
export default function HomeScreen() {
  const dispatch = useDispatch();
  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;


  const blogList = useSelector((state) => state.blogList);
  const { blogs } = blogList;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);
  console.log(products);
  return (
    <div>
      <div className="h-[100vh] flex justify-center items-center bg-[red]">
        {/* <SliderHome /> */}
        <p className="text-[60px] text-center">HOME PAGE</p>
        {/* <HomeEvents /> */}
      </div>
      <div className='w-[80%] m-[auto] flex justify-between mt-[20px] events'>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <div className="flex justify-between flex-wrap">
          {products.slice(0,3).map((product) => (
            <Product key={product._id} product={product} />
          ))}
        </div>
      )}
    </div>
      <Testimontials/>
      <div className='w-[80%] m-[auto] flex justify-between mt-[20px] events'>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <div className="flex justify-between flex-wrap">
          {blogs.slice(0,3).map((product) => (
            <Blog key={blog._id} blog={blog} />
          ))}
        </div>
      )}
    </div>
      <Subscribe />
    </div>
  );
}
