import React, { useEffect } from 'react';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { useDispatch, useSelector } from 'react-redux';
import { listGalleries } from '../actions/galleryActions';
import Gallery from '../components/Gallery';

export default function GalleriesSection() {
  const dispatch = useDispatch();
  const galleryList = useSelector((state) => state.galleryList);
  const { loading, error, galleries } = galleryList;

  useEffect(() => {
    dispatch(listGalleries());
  }, [dispatch]);


  console.log(galleries)

  return (

<div>
    <div className='flex justify-center items-center bg-[#C4C4C4] text-center text-5xl font-bold p-[10px]'>
      <p className='text-center'>
    Galeries
      </p></div>
<div className='w-[80%] m-[auto] flex justify-between mt-[20px] events'>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <div className="flex justify-between flex-wrap">
          {/* {galleries.map((gallery) => (
            <Gallery key={gallery._id} gallery={gallery} />
          ))} */}
        </div>
      )}
    </div>
</div>
  );
}
