import React from "react";
import styled from "styled-components";
import { BsFillPlayFill } from "react-icons/bs";


const Container = styled.div`
  min-width: 480px;
  height: 280px;
  display: flex;
  justify-content: center;
  flex-direction: column;
  position: relative;
  margin: 20px;
  position: relative;
  :hover {
    cursor: pointer;
  }

`;
const Image = styled.img`
  height: 90%;
  width: 90%;
  object-fit: cover;
`;
const Button = styled.button`
  position: relative;
  font-size: 15px;
  color: white;
  border: none;
  padding: 30px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  font-weight: 500;
  z-index: 100;
  background: #256f98;
  transition: all 0.5s ease;
  &:hover {
    background: #0492c4;
  }
`;
const PlayContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  
`;
const InfoContainer = styled.div`
  padding: 10px;
  width: 90%;
  background: #256f98;
  color: white;
  font-weight: 500;
`;

const RecordItem = ({ record }) => {
  return (
    <Container>
      <Image src={record.image}></Image>
      <InfoContainer>{record.title} </InfoContainer>
      <PlayContainer>
        <Button>
          PLay
         </Button>
      </PlayContainer>
    </Container>
  );
};

export default RecordItem;
