import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { detailsBook } from "../actions/bookActions";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { BOOK_REVIEW_CREATE_RESET } from "../constants/bookConstants";
import { MdDateRange } from "react-icons/md";

export default function ProductScreen(props) {
  const dispatch = useDispatch();
  const bookId = props.match.params.id;
  const bookDetails = useSelector((state) => state.bookDetails);
  const { loading, error, book } = bookDetails;
  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;

  useEffect(() => {
    dispatch(detailsBook(bookId));
  }, [dispatch, bookId]);

  return (
    <Wrapper>
      <div className="w-[90%] m-[auto] center">
        {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : (
          <div className="">
            <Link path="/" className="text-[red] text-[25px] font-[bold] ">
              Ana Sehifeye Geri Don
            </Link>
            <div className="bookscreendiv">
              <div className="mt-[20px] productimgdiv">
                <img
                  className="m-[auto]"
                  src={book.image}
                  alt={book.title}
                ></img>
              </div>
              <div>
                <div className="p-[20px] producttitle">
                  <h2 className="font-bold text-[30px]">{book.title}</h2>
                  <p>{book.decription}</p>
                </div>
                <hr />
                <div className="w-[100%] p-[15px] m-[auto]  flex justify-between datesection ">
                  <div className="flex items-center justify-between w-[100px] date">
                    <div>
                      <MdDateRange />
                    </div>
                    <div className="font-bold ">22.01.2020</div>
                  </div>

                  <div className="bg-[#256F98] p-[10px] text-[white]">
                    <div className="applybtn  pointer">Apply product</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  .bookscreendiv {
    width: 60%;
    display: flex;
    justify-content: space-between;
  }
  .productimgdiv {
    background: red;
    border: 1px solid red;
  }
  .productimgdiv img {
    width: 500px;
    height: 500px;
  }

  .productname {
    font-size: 45px;
    color: black;
    padding: 0px;
    margin: 0px;
  }

  @media (max-width: 768px) {
    .productname {
      font-size: 15px;
    }
  }
`;
