import React from "react";
import styled from "styled-components";
import { AiOutlineUser } from "react-icons/ai";
import {FaBlogger} from 'react-icons/fa';
import {BsImage, BsCalendar2EventFill,BsFillRecordBtnFill,BsBookshelf} from 'react-icons/bs';

const DashboardScreen = () => {
  return (
    <Wrapper>
      <div className="dashboxes">
        <div className="dasbox">
          <div className="icon">
            <AiOutlineUser />
          </div>

          <p>Total Users</p>
          <h4>1,000</h4>
        </div>
        <div className="dasbox">
          <div className="icon">
            <BsCalendar2EventFill />
          </div>

          <p>Total Events</p>
          <h4>1,000</h4>
        </div>
        <div className="dasbox">
          <div className="icon">
            <FaBlogger />
          </div>

          <p>Total Blogs</p>
          <h4>1,000</h4>
        </div>
        <div className="dasbox">
          <div className="icon">
            <BsBookshelf />
          </div>

          <p>Total Books</p>
          <h4>1,000</h4>
        </div>
        <div className="dasbox">
          <div className="icon">
            <BsFillRecordBtnFill />
          </div>

          <p>Total Records</p>
          <h4>1,000</h4>
        </div>
        <div className="dasbox">
          <div className="icon">
            <AiOutlineUser />
          </div>

          <p>Total Gallery Images</p>
          <h4>1,000</h4>
        </div>
      </div>
    </Wrapper>
  );
};

export default DashboardScreen;

const Wrapper = styled.div`
  width: 90%;
  margin: auto;
  background: grey;
  .dashboxes {
    width: 60%;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
  }
  .dasbox {
    width: 250px;
    height: 200px;
    background: white;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
    border-radius: 5px;
    margin: 10px;
    color: black;
    padding: 10px;
  }
  .dasbox:hover {
    background: black;
    color: white;
  }

  .icon {
    width: 70px;
    height: 70px;
    border-radius: 5px;
    background: blue;
    padding: 10px;
    font-size: 45px;
  }
`;
